
public class Number10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=-100;
		while(i<=100)
		{
			if(i%2==0)
			{
				if(i>=1)
				{
					System.out.println(i);
				}
				
			}
			i++;
			
		}
		int a=-100;
		while(a<=100)
		{
			if(a%2==1)
			{
				if(a<=-1)
				{
					System.out.println(a);
				}
			}
			a++;
		}
		
			

	}

}
